import React, { useState, useEffect } from "react";
import WeekBarGraph from './WeekBarGraph';
import ThirtyDaysMonthBarGraph from './ThirtyDaysMonthBarGraph';
import ThirtyOneDaysMonthBarGraph from './ThirtyOneDaysMonthBarGraph';
import FebLeapMonthBarGraph from './FebLeapMonthBarGraph';
import FebMonthBarGraph from './FebMonthBarGraph';
import TaskMeter from './TaskMeter';
import TimeSelect from './TimeSelect';
import YearLineChart from "./YearLineChart";
import CurrentWeekButton from "./CurrentWeekButton";



function Dashbaord(props) {

    // const [isShown, setIsShown] = useState(1);
    const [status, setStatus] = useState(0);

    function swap(value){
        console.log(value);
        setStatus(value);
    }

    return (
        <div>
            <div className="row">
                {/* <div className="col-lg-8 col-md-7 col-sm-8"><div className="bargraphdiv"><YearLineChart /></div></div> */}
                {console.log(status)}

                { status ? <div className="col-lg-8 col-md-7 col-sm-8"><div className="bargraphdiv"><WeekBarGraph /></div></div> 
                : <div className="col-lg-8 col-md-7 col-sm-8"><div className="bargraphdiv"><YearLineChart /></div></div> }

                <div className="col-lg-4 col-md-4 col-sm-4"><div className="timeselect"><TimeSelect isShown={isShown} setIsShown={setIsShown}/></div></div>

            </div>

            <div className="row">

                <div className="col-lg-8 col-md-8 col-sm-8"><div className="taskmeterdiv" style={{fontSize: 21, textAlign: 'center', color: 'white', marginTop: '5vh', marginLeft: '5vh', }}>
                <TaskMeter label="Bugs fixed" value={25}/></div></div>
                <div className="col-lg-4 col-md-4 col-sm-4"><CurrentWeekButton onClick={handleClick}/></div>



            </div>


    </div>
  );
}

export default Dashbaord;
