import * as Constants from "../constants.js";
import axios from 'axios';

async function EditUserPreferencesApi(data) {

  const uid = localStorage.getItem('uid');
  const accessToken = localStorage.getItem('accessToken');
  const url = Constants.base_url + '/auth/editPreference/' + uid;
  const bearerToken = 'Bearer ' + accessToken;
  // console.log(sortBy);
  // const url= Constants.base_url + '/auth/bug/created/'+ uid;

  // console.log(uid);
  // console.log(accessToken);
  // console.log(url);
  let status = 400;
  console.log("Data: ", data);
  await fetch(url, {
    method: 'POST',  //or 'PUT'
    mode: 'cors',
    body: data,
    // headers: {
    //   'Authorization': bearerToken,
    //   'x-access-token': accessToken,
    //
    // },
     // body: JSON.stringify(data),
  })
    .then((response) => response.json())
    .then((data) => {
      console.log("Edit preferences");
      console.log(data);
      status = 200;
    })
    .catch((error) => {
      console.error('Error:', error);
    });
    return status;
}

export default EditUserPreferencesApi;
