//     Tooltip,
//     Legend,
//   } from 'chart.js';

import {Line} from "react-chartjs-2";
import Chart from 'chart.js/auto';

// ChartJS.register(
//     CategoryScale,
//     LinearScale,
//     PointElement,
//     LineElement,
//     Title,
