import * as Constants from "../constants.js";
import axios from 'axios';

async function bugInfoApi(bugid) {

  var url = Constants.base_url + "/bug/" + bugid;
  let bugInfo = [];

  var config = {
    method: 'get',
    url: url,
    headers: {}
  };

  axios(config).then(function(response) {
    // console.log(response.data, typeof(response.data["bugs"]));
    // console.log(response.data.bugs[0]);
    bugInfo = response.data.bugs[0];
  }).catch(function(error) {
    console.log(error);
  });
  console.log(bugInfo);
  return bugInfo
}

export default bugInfoApi;
